import { DataService } from './../../service/data.service';
import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';


@Component({
  selector: 'app-product-detail',
  templateUrl: './product-detail.component.html',
  styleUrls: ['./product-detail.component.scss']
})
export class ProductDetailComponent {
  product: any;
  selectedMemory = [];
  img: string|any;
  selectedColor: string|any;
  selectedPrice: number|any;
  sideImages = [];
  id: number|null=null;
  constructor(private router: ActivatedRoute, private DataService:DataService) {

  }
  ngOnInit(): void {
		let MyId = this.router.snapshot.paramMap.get("id");
		this.load_product(MyId);
	}
  load_product(id:any){
    this.DataService.get(`/products/${id}`).then((res:any)=>{
      this.product= res.data;
      this.img = res.image[0].path[0].img;
      this.sideImages = res.image[0].path;
      this.selectedColor = res.image[0].phoneColor;
      this.selectedMemory = res.memory[0].size;
      this.selectedPrice = res.memory[0].price;
    })
  }
  sideImage(images:any) {
    this.img = images;
  }

  selectedPhoneColor(data:any) {
    this.img = data.path[0].img;
    this.selectedColor = data.phoneColor;
    this.sideImages = data.path;
  }
  selectPhoneMemory(data) {
    this.selectedMemory = data.size;
    // we change price when memory size is changed
    this.selectedPrice = data.price;
  }


}
